import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import GetStartedViewHandler from "./GetStartedViewHandler";

// Define Intro component here
function Intro() {
  return (
    <>
      <div className="Motto">
        <img
          src="./hospitalCalendarLogo.svg"
          style={{ height: "120px" }}
          alt="appLogo"
        />
        Let us help you Streamline Medical Event Planning
      </div>
      <div className="StatementContainer">
        <Statement text="Easily create/update events" />
        <Statement text="Automatic Notifications" />
        <Statement text="Easily create/update events" />
      </div>
    </>
  );
}

// Define Statement component here
function Statement({ text }) {
  return (
    <>
      <img src="./hollowCircle.svg" style={{ height: "100%" }} alt="bullet" />
      <div>{text}</div>
    </>
  );
}

function GetStarted() {
  const { form, login, signup } = GetStartedViewHandler();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Make a POST request to the backend API endpoint
      const response = await axios.post('http://localhost:8080/api/signup/register', formData);
      console.log('User registered:', response.data);
      // Redirect to success page or any other page after successful registration
      navigate("/success");
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  return (
    <div className="GetStartedWrapper">
      <div className="GetStarted">
        <div className="Intro"><Intro /></div> {/* Include Intro component here */}
        <div className="Options">
          <form className="GetStartedForm" ref={form} onSubmit={handleSubmit}>
            <input type="text" placeholder="First Name" name="firstName" value={formData.firstName} onChange={handleChange} />
            <input type="text" placeholder="Last Name" name="lastName" value={formData.lastName} onChange={handleChange} />
            <input type="email" placeholder="Email" name="email" value={formData.email} onChange={handleChange} />
            <input type="password" placeholder="Password" name="password" value={formData.password} onChange={handleChange} />
            <input type="password" placeholder="Confirm Password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} />
            <input type="submit" value="SIGNUP" />
          </form>
        </div>
      </div>
    </div>
  );
}

export default GetStarted;
